function hexToRgb(n){n=n.replace(/^#?([a-f\d])([a-f\d])([a-f\d])$/i,function(a,t,o,c){return t+t+o+o+c+c});var r=/^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(n);return r?{r:parseInt(r[1],16),g:parseInt(r[2],16),b:parseInt(r[3],16)}:null}function luminance(n,e,r){var a=[n,e,r].map(function(t){return(t/=255)<=.03928?t/12.92:Math.pow((t+.055)/1.055,2.4)});return.2126*a[0]+.7152*a[1]+.0722*a[2]}function calculateRatio(n,e){const r=hexToRgb(n),a=hexToRgb(e),t=luminance(r.r,r.g,r.b),o=luminance(a.r,a.g,a.b),c=t>o?(o+.05)/(t+.05):(t+.05)/(o+.05);document.querySelector("[data-ratio]").setAttribute("data-ratio",c)}